using UnityEngine;public class IridescenceThinFilm : MonoBehaviour{	public bool m_ApplyChildren = false;	[Range(0f, 32f)] public float m_FilmStrength = 1f;	[Range(0.1f, 18f)] public float m_FilmFrequency = 10f;	public Renderer m_Rd;		void Start ()	{		m_Rd = GetComponent<Renderer> ();	}	void Update ()	{		if (m_ApplyChildren)		{			Renderer[] rds = GetComponentsInChildren<Renderer> ();			for (int i = 0; i < rds.Length; i++)			{				rds[i].material.SetFloat ("_Thinfilm_Strength", m_FilmStrength);				rds[i].material.SetFloat ("_Thinfilm_Color_Freq", m_FilmFrequency);			}		}		else		{			SetMaterialsFloat ("_Thinfilm_Strength", m_FilmStrength);			SetMaterialsFloat ("_Thinfilm_Color_Freq", m_FilmFrequency);		}	}	void SetMaterialsFloat (string name, float f)	{		Material[] mats = m_Rd.materials;		for (int i = 0; i < mats.Length; i++)		{			mats[i].SetFloat (name, f);		}	}}